// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IVoter.sol";

contract BallotContract {
    struct Candidate {
        uint id;
        string name;
        uint voteCount;
    }

    struct Election {
        uint id;
        string name;
        mapping(uint => Candidate) candidates;
        uint candidatesCount;
        bool isActive;
        uint totalVotes;
    }

    mapping(uint => Election) public elections;
    uint public electionsCount;

    event ElectionCreated(uint id, string name);
    event VoteCast(uint electionId, uint candidateId);

    function createElection(string memory _name) public {
        electionsCount++;
        Election storage newElection = elections[electionsCount];
        newElection.id = electionsCount;
        newElection.name = _name;
        newElection.isActive = true;

        emit ElectionCreated(electionsCount, _name);
    }

    function addCandidate(uint _electionId, string memory _name) public {
        require(elections[_electionId].isActive, "Election is not active");
        Election storage election = elections[_electionId];
        election.candidatesCount++;
        election.candidates[election.candidatesCount] = Candidate(election.candidatesCount, _name, 0);
    }

    function vote(uint _electionId, uint _candidateId) public {
        require(elections[_electionId].isActive, "Election is not active");
        Election storage election = elections[_electionId];
        require(_candidateId > 0 && _candidateId <= election.candidatesCount, "Invalid candidate ID");

        election.candidates[_candidateId].voteCount++;
        election.totalVotes++;

        emit VoteCast(_electionId, _candidateId);
    }

    function tallyVotes(uint _electionId) public view returns (uint winningCandidateId) {
        require(!elections[_electionId].isActive, "Election is still active");
        uint winningVoteCount = 0;

        for (uint i = 1; i <= elections[_electionId].candidatesCount; i++) {
            if (elections[_electionId].candidates[i].voteCount > winningVoteCount) {
                winningVoteCount = elections[_electionId].candidates[i].voteCount;
                winningCandidateId = i;
            }
        }
    }

    function closeElection(uint _electionId) public {
        elections[_electionId].isActive = false;
    }
}

